mapper.select:
select aav.*, ai.issue_id, concat_ws( '-', api.project_code, ai.issue_num ) AS issue_num, ai.summary, ai.assignee_id, ai.status_id, fs.name as status_name, ai.priority_id, fp.name as priority_name, apv.version_id as version_id, apv.name as version_name from agile_app_version aav left join agile_app_version_issue_rel aavir on aav.id = aavir.app_version_id left join agile_issue ai on ( ai.project_id in
#{projectId}
and ai.issue_id = aavir.issue_id and ai.type_code = 'bug' and ai.status_id in ( select status_id from agile_issue_status where project_id in
#{projectId}
and is_completed = 1 ) ) left join agile_project_info api on ai.project_id = api.project_id left join fd_status fs on ai.status_id = fs.id left join fd_priority fp on (fp.organization_id = #{organizationId} and ai.priority_id = fp.id) left join agile_version_issue_rel avir on (ai.project_id = avir.project_id and avir.relation_type = 'influence' and ai.issue_id = avir.issue_id) left join agile_product_version apv on (ai.project_id = apv.project_id and avir.version_id = apv.version_id) where aav.organization_id = #{organizationId} and aav.project_id in
#{projectId}
and aav.id in
#{appVersionId}
and concat_ws( '-', api.project_code, ai.issue_num) != ''

mapper.delete:
DELETE
FROM agile_app_version_issue_rel
WHERE
project_id = #{projectId}
AND issue_id = #{issueId}
AND app_version_id IN

    #{appVersionId}
