mapper.select:
select *
from agile_tag_compare_history
where organization_id = #{organizationId}
and project_id = #{projectId}
and app_service_code in

    #{appServiceCode}

SELECT
    atch.*
FROM
    agile_tag_compare_history atch
WHERE
    atch.id IN (
        SELECT
        MAX(atch1.id)
        FROM agile_version_tag_history avth
        JOIN agile_tag_compare_history atch1 ON avth.id = atch1.version_tag_history_id
        WHERE avth.organization_id = #{organizationId}
        AND avth.project_id = #{projectId}
        AND avth.version_id = #{versionId}
        AND avth.version_type = #{versionType}
        GROUP BY atch1.app_service_code
    )
