mapper.select:
SELECT
    awl.log_id,
    awl.work_time,
    awl.start_date,
    awl.created_by
FROM
    agile_work_log awl
JOIN agile_issue_sprint_rel aisr ON awl.issue_id = aisr.issue_id AND aisr.project_id = #{projectId}
WHERE
    aisr.sprint_id = #{sprintId}
AND awl.start_date >= #{startDate}
AND awl.start_date <= #{endDate}
